import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Form, Button, FloatingLabel } from 'react-bootstrap'
import React, { Component } from 'react'

class Signup extends Component {
    constructor(props) {
      super(props)

      this.state = {
        name:'',
        username:'',
        email:'',
        pwd:''

    }
    }
    submitHandle = e => {
        e.preventDefault();
        console.log(this);
    }
  render() {
    // const {name, username, email, pwd} = this.state;

    return (
        <Form className='m-5' onSubmit= {this.submitHandle} >
            <h1 className='text-center display-4'> Sign Up</h1>
            <FloatingLabel controlId="floatingInput" label="Name" className='mb-3 '>
                <Form.Control type="text" placeholder="Name"  onChange={this.submitHandle} />
            </FloatingLabel>
            <FloatingLabel controlId="floatingInput" label="User Name" className='mb-3' >
                <Form.Control type="text" placeholder="UserName"  onChange={this.submitHandle} />
            </FloatingLabel>

            <FloatingLabel controlId="floatingInput" label="Email" className='mb-3' >
                <Form.Control type="email" placeholder="name@example.com" onChange={this.submitHandle} />

            </FloatingLabel>

            <FloatingLabel controlId="floatingInput" label="Password" className='mb-3'>
                <Form.Control type="password" placeholder="Password"  onChange={this.submitHandle} />
            </FloatingLabel>

            <Button className='mt-3' variant="primary" type="submit">
                Submit
            </Button>
        </Form>
    )
  }
}

export default Signup;
