import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Form, Button, FloatingLabel, Alert } from 'react-bootstrap'
import React, { Component } from 'react'
let message = '';
let success = false;
let error = false;

class Signup extends Component {

    constructor(props) {
        super(props)

        this.state = {
            name: '',
            username: '',
            email: '',
            password: ''

        }
    }

    submitHandle = e => {


        e.preventDefault();
        console.log(this.state);

        axios.post('https://sutt-front-task-one.herokuapp.com/api/v1/auth/register', this.state)

            .then(response => {
                message = 'success'
                success = true;

            })
            .catch(error => {
                message = JSON.parse(error.request.response).message;
                console.log(message);
                error = true;

            })

    }
    changevalue = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    alertnotif = click =>{
        if (click){
            return 'a';
        }
        else{
            return 'b';
        }
    }

    render() {
        // const {name, username, email, pwd} = this.state;

        return (
            <>
                {success?(
                    <Alert className='m-5' variant='success'>
                        Successfully Logged In
                    </Alert>
                ):( <div>
                        <Form className='m-5' onSubmit={this.submitHandle} >
                            <h1 className='text-center display-4'> Sign Up</h1>
                            <FloatingLabel controlId="floatingInput" label="Name" className='mb-3 '>
                                <Form.Control type="text" name='name' placeholder="Name" onChange={this.changevalue} />
                            </FloatingLabel>
                            <FloatingLabel controlId="floatingInput" label="User Name" className='mb-3' >
                                <Form.Control type="text" name='username' placeholder="UserName" onChange={this.changevalue} />
                            </FloatingLabel>

                            <FloatingLabel controlId="floatingInput" label="Email" className='mb-3' >
                                <Form.Control type="email" name='email' placeholder="name@example.com" onChange={this.changevalue} />

                            </FloatingLabel>

                            <FloatingLabel controlId="floatingInput" label="Password" className='mb-3'>
                                <Form.Control type="password" name='password' placeholder="Password" onChange={this.changevalue} />
                            </FloatingLabel>

                            <Button className='mt-3' variant="primary" type="submit">
                                Submit
                            </Button>

                        </Form>
                        {error?(<Alert className='m-5' variant='danger'>
                            {message}
                        </Alert>):<></>}
                    </div>

                )

                }
            </>

        )
    }
}

export default Signup;
