import { React, useEffect, useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Form, Button, FloatingLabel } from 'react-bootstrap';

function Login(){

    const [username,setUsername] = useState("");
    const [password,setPassword] = useState("");
    const [errMsg, setErrMsg] = useState("");
    const [success, setSuccess] = useState(false);

    function onlogin(e){
        e.preventDefault();
        console.log(username)
        console.log(password)
        axios.post('https://sutt-front-task-one.herokuapp.com/api/v1/auth/login', this.state)

            .then(response => {
                message = 'success'
                clicked = true;

            })
            .catch(error => {
                message = JSON.parse(error.request.response).message;
                console.log(message);
                clicked = true;
                console.log(clicked)

            })
    }

    return(
    <Form className='m-5' onSubmit={onlogin}>
        <h1 className='text-center display-4'> Login</h1>

        <FloatingLabel controlId="floatingInput" label="Username" className='mb-3'>
                <Form.Control type="text" placeholder="UserName" onChange={(e) => setUsername(e.target.value)}/>

        </FloatingLabel>
        <FloatingLabel controlId="floatingInput" label="Password" className='mb-3'>
                <Form.Control type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)}/>

        </FloatingLabel>
        <div>
            <Button className='mt-3 text-center' variant="primary" type="submit">
                Login 
            </Button>

            <a href="/SignUp" className='mx-3'>SignUp</a>
        </div>


    </Form>)
}

export default Login;
