import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Form, Button, FloatingLabel } from 'react-bootstrap'
import React, { Component } from 'react'

class Signup extends Component {
    constructor(props) {
      super(props)

      this.state = {
        name:'',
        username:'',
        email:'',
        pwd:''

    }
    }
    changeHandle = e =>{
        this.setState({[e.target.label]: e.target.value})
    }
    submitHandle = e => {
        e.preventDefault();
        console.log(this);
    }
  render() {
    const {name, username, email, pwd} = this.state;

    return (
        <Form className='m-5' onSubmit= {this.submitHandle} >
            <h1 className='text-center display-4'> Sign Up</h1>
            <FloatingLabel controlId="floatingInput" label="name" className='mb-3 ' value ={name} onChange={this.submitHandle}>
                <Form.Control type="text" placeholder="Name" />
            </FloatingLabel>
            <FloatingLabel controlId="floatingInput" label="username" className='mb-3' value={username} onChange={this.submitHandle}>
                <Form.Control type="text" placeholder="UserName" />
            </FloatingLabel>

            <FloatingLabel controlId="floatingInput" label="email" className='mb-3' value={email} onChange={this.submitHandle}>
                <Form.Control type="email" placeholder="name@example.com" />

            </FloatingLabel>

            <FloatingLabel controlId="floatingInput" label="pwd" className='mb-3' value={pwd} >
                {/* <Form.Control type="password" placeholder="Password" /> */}
            </FloatingLabel>

            <Button className='mt-3' variant="primary" type="submit">
                Submit
            </Button>
        </Form>
    )
  }
}

export default Signup;
