import { render } from '@testing-library/react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import { Alert,Card, FloatingLabel } from 'react-bootstrap';
import { React, useEffect, useState } from 'react';

function Dashboard(){
    const [dashboard,setDashboard] = useState(true)
    function getinfor() {
        const api = 'https://sutt-front-task-one.herokuapp.com/api/v1/auth/user';
        const user = JSON.parse(sessionStorage.getItem('data'));
        const token = user.data.id; /*take only token and save in token variable*/
        axios.get(api, { headers: { "Authorization": `Bearer ${token}` } })

            .then(res => {
                console.log(res.data);
            })
            .catch((error) => {
                console.log(error)
            });
    }

    return(
        <>
            {dashboard ?(
                <Card className='m-4 p-3 text-center'>
                    <Alert variant='success'>
                        Successfully Logged In
                    </Alert>
                    <Card.Title>Name : { }</Card.Title>
                    <Card.Subtitle className="mb-2 text-muted">Email ID : { }</Card.Subtitle>
                    <Card.Subtitle className="mb-2 text-muted">User Name : { }</Card.Subtitle>
                    <Card.Text>
                        Created at { }
                        Updated at { }
                    </Card.Text>
                </Card>):(getinfor())}
        </>

    )

}


export default Dashboard;
